.click(function() {        var id = this.id;        $.ajax({            type: "POST",            url: "<?php echo site_url(); ?>" + "/sector/delete",            data: {'int_skill_id': id},            datatype: "json",            success: function(result) {                var obj = $.parseJSON(result);                if (obj.success)                    alert(obj.msg);                window.location.reload();            },            error: function() {                console.log("Some Error Occured");            }        });    });    $('#save_product').click(function() {        save_data();    });    $('#form_modal').submit(function() {        save_data();    });    function save_data() {        var data = $('#form_modal').serialize();        $.ajax({            type: "POST",            url: "<?php echo site_url(); ?>" + "/skill/add",            data: data,            datatype: "json",            success: function(result) {                var obj = $.parseJSON(result);                alert(obj.msg);                if (obj.success)                    window.location.reload();            },            error: function() {                console.log("Some Error Occured");            }        });    }</script>